func TestUptime(t *testing.T) {
	stdOs = std.NewMockOs()

	stdOs.(std.MockOs).MockFile("/proc/stat", []byte(procStat))

	time1 := time.Now()
	if result, err := impl.Export("system.uptime", []string{}, nil); err != nil {
		t.Errorf("system.uptime returned error %s", err.Error())
	} else {
		if uptime, ok := result.(int); !ok {
			t.Errorf("system.uptime returned unexpected value type %s", reflect.TypeOf(result).Kind())
		} else {
			time2 := time.Now()
			// handle the possibility that clock second changed during the uptime calculation
			if time2.Unix()-int64(uptime) != 1234567890 && time1.Unix()-int64(uptime) != 1234567890 {
				t.Errorf("system.uptime returned invalid result")
			}
		}
	}
}
